import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-victimcalc',
  templateUrl: './victimcalc.component.html',
  styleUrls: ['./victimcalc.component.css']
})
export class VictimcalcComponent {

  ageAtDeathA: number | undefined;
  yearOfDeathA: number | undefined;
  ageAtDeathB: number | undefined;
  yearOfDeathB: number | undefined;
  showAlert: boolean = false;
  showAnswer: boolean = false;
  averageValue: any;
  tahunke: any;
  constructor(private http: HttpClient) {}

  calc() {
    if (!this.ageAtDeathA || !this.yearOfDeathA || !this.ageAtDeathB || !this.yearOfDeathB) {
      // alert('Please fill in all fields');
      this.showAlert = true;
      this.showAnswer = false;
      return;
    }

    this.showAlert = false;
    const body = {
      "ageAtDeathA": this.ageAtDeathA,
      "yearOfDeathA": this.yearOfDeathA,
      "ageAtDeathB": this.ageAtDeathB,
      "yearOfDeathB": this.yearOfDeathB
    };

    this.http.post<any>("http://localhost:8087/api/v1/victim/average", body)
      .subscribe(
        (response) => {
          // Handle successful response
          console.log('Response:', response);
          if(response.status == '200 OK'){
            this.showAnswer = true;
            this.tahunke = response.payload.personA_jumlahKematian;
            this.averageValue = response.payload.average;
            console.log('Average:', this.averageValue);
          }else{
            this.showAnswer = false;
          }

        },
        (error) => {
          // Handle error
          console.error('Error:', error);
        }
      );
  }


}
<div class="container">
    <div class="mt-5">
        <div class="card">
            <div class="card-header">
              Witched Victim (calc)
            </div>
            <div class="m-3">
                <div *ngIf="showAlert" class="alert alert-warning" role="alert">
                    Input fields are required.
                </div>
            </div>
            <div class="row">
                <div class="col-md-6">
                    <div class="card-body">
                        <div class="form-group">
                          <label for="ageAtDeathA">Age at DeathA</label>
                          <input type="number" class="form-control" id="ageAtDeathA" [(ngModel)]="ageAtDeathA">
                        </div>
                    </div>
                </div>
                <div class="col-md-6">
                    <div class="card-body">
                        <div class="form-group">
                          <label for="yearOfDeathA">Year Of DeathA</label>
                          <input type="number" class="form-control" id="yearOfDeathA" [(ngModel)]="yearOfDeathA">
                        </div>
                    </div>
                </div>
                <div class="col-md-6">
                    <div class="card-body">
                        <div class="form-group">
                          <label for="ageAtDeathB">Age at DeathB</label>
                          <input type="number" class="form-control" id="ageAtDeathB" [(ngModel)]="ageAtDeathB">
                        </div>
                    </div>
                </div>
                <div class="col-md-6">
                    <div class="card-body">
                        <div class="form-group">
                          <label for="yearOfDeathB">Year Of DeathB</label>
                          <input type="number" class="form-control" id="yearOfDeathB" [(ngModel)]="yearOfDeathB">
                        </div>
                    </div>
                </div>
                <div class="container" *ngIf="showAnswer">
                    <div class="m-2 p-4" style="background: #20c997;height: 12rem;">
                        <b>Asnwer</b> <br> 
                        Victim Average is : <b>{{averageValue}}</b> 
                        <br>rata - rata diambil berdasarkan  
                        Pada Tahun ke : <b>{{tahunke}}</b> <br> 
                        <!-- Ditemukan Korban Sebanyak  <b>{{}}</b> Orang-->
                    </div>
                </div>
                <div class="col-md-12 p-3 m-2">
                    <div class="btn btn-primary mt-3" (click)="calc()">Calculate</div>
                </div>
            </div>
        </div>
    </div>
</div>
